document.addEventListener("DOMContentLoaded",(function(){const t=document.querySelector('meta[name="csrf-token"]').content;document.getElementById("btn-cookies-consent").onclick=async function(){const n="true"===this.dataset.consent,e=await fetch("/cookies/consent",{method:"POST",headers:{"Content-Type":"application/json","X-CSRF-Token":t},body:JSON.stringify({consent:n})});e.ok?(this.dataset.consent=n,this.textContent=`Cookie consent has been updated to: ${n}.`):await e.json()}}),{once:!0});